function onChange(control, oldValue, newValue, isLoading) {
   if (newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   //var business_user= g_form.getReference('business_user', setManager);
	var business_user=g_form.getValue('business_user');
	var glideMTA = new GlideAjax('GetManager');
    glideMTA.addParam('sysparm_name','fetchData');
    glideMTA.addParam('sysparm_business_user',business_user);
	alert("TEM  populate approver");
    glideMTA.getXML(callback);
    function callback(response)
    {
       var managerFromSI= response.responseXML.documentElement.getAttribute("answer");
        g_form.setValue('request_approver',managerFromSI);//change your field name here
    }	
}
