sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectRitm', function(event,data) {
		c.server.get({
			action: 'getRitm',
			ritmID: $rootScope.ritmID
		}).then(function(r) {
			console.log('selectRitm return :' + r.data.ritm.sys_id); 

			c.data.ritmID = r.data.ritm.sys_id;
			c.data.number = r.data.ritm.number;
			c.data.due_date = r.data.ritm.due_date;
			c.data.short_description = r.data.ritm.short_description;
		});
	});

	c.updateRitm = function(updateType) {
		console.log('updateRitm called number:' + c.data.number); 
		console.log('updateRitm called sys_id:' + c.data.ritmID); 
		c.server.get({
			action: 'updateRitm',
			ritmID: c.data.ritmID,
			ritmNum: c.data.number,
			ritmDue: c.data.due_date,
			ritmShortDesc: c.data.short_description
		}).then(function(r) {
			// When the promise is returned, emit the change
			if (updateType == 'number' || updateType == 'short_description') { 
				$rootScope.$emit('updateRitm', r.data); 
			}
		});
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if (input && input.ritmID) {
		console.log('body server script called ' + input.ritmID );
		var ritm = new GlideRecord('sc_req_item');
		if (ritm.get(input.ritmID)) {
			if (input.action == 'getRitm') {
				data.ritm = {};
				$sp.getRecordValues(data.ritm, ritm, "sys_id,number,due_date, short_description");
			}
			else if (input.action == 'updateRitm') { 
				//ritm.number = input.ritmNum; 
				//ritm.due_date = input.ritmDue; 
				console.log('tem short_description' + input.ritmShortDesc);
				ritm.short_description = input.ritmShortDesc;
				ritm.update(); 

				// Need to update the data object with the new title
				data.short_description = ritm.getValue('short_description');
			}
		}
	}

})();
