sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading) {
		return;
	}

	if (newValue === '') {
		g_form.clearValue('price');
		g_form.clearValue('recurring_price');

	}
	var addons_arr = g_form.getValue('mandatory_addons').split(',');

	var total_price = 0;
	var total_recurring = 0;
	addons_arr.forEach(function (addon, index) {
		var getComponentPrice = new GlideAjax('GetPlanComponentPrice');
		getComponentPrice.addParam('sysparm_name','mandatoryPlanComponentPrice');
		getComponentPrice.addParam('sysparm_addons_sys_id', addon);

		// Send the request to the server
		getComponentPrice.getXML(populatePriceField);
	});		

	// When the response is back from the server
	function populatePriceField(response){
		// Extract the price value from the response, clear any value from the email field, 
		// set new value in the email field
		var priceFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		alert('priceFromScriptInclude ' +priceFromScriptInclude);
		var price_arr = priceFromScriptInclude.split(';');		 
		total_price += parseFloat(price_arr[0]);	
		total_recurring += parseFloat(price_arr[1]);
		g_form.clearValue('price');
		g_form.setValue('price',total_price);
		g_form.clearValue('recurring_price');
		g_form.setValue('recurring_price',total_recurring);

	}

}
