var VendorByCountry = Class.create();
VendorByCountry.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    getVendor: function() {

	var country = this.getParameter('sysparm_country');
	var vendor= new GlideRecord('x_58872_needit_vendor');

        vendor.addQuery('country',country);
        vendor.query();
        gs.info("q country is " + country);
        while(vendor.next()) {
          gs.info("n vendor is " + vendor.name);
          return vendor.name;
        }

    },
    type: 'VendorByCountry'
});
