sys_script_include.script:
var GetManager = Class.create();

GetManager.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	fetchData: function() {

		var business_user = this.getParameter('sysparm_business_user');

		var gr = new GlideRecord('sys_user');

		gr.addQuery('user_name', business_user);
		//gr.addQuery('sys_id',business_user);
		//gr.addQuery('name',business_user);
		gr.query();
		gs.info("query executed "+business_user);
		if (gr.next()){
			gs.info("manager for " + business_user + " is " + gr.manager);
			return gr.manager;
		}
	},

	type: 'GetManager'
});
