sys_script_include.script:
var GetPlanComponentPrice = Class.create();
GetPlanComponentPrice.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	mandatoryPlanComponentPrice: function(addons_s) {
		var addons_u = this.getParameter('sysparm_addons_sys_id');
		// make script callable from client and server
		var gr_addon = new GlideRecord('x_58872_needit_plan_add_on');
		if (addons_u) {
			gr_addon.get(addons_u);
		} else {
			gr_addon.get(addons_s);
		}		

		var price = parseFloat(gr_addon.price);
		var recurring_price = parseFloat(gr_addon.recurring_price);

		gs.info('getPlanComponentPrice '+ price + ';' + recurring_price);
		return price + ';' + recurring_price;
	},
	type: 'GetPlanComponentPrice'
});
